
public class cs {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		String s="Abcd";
		if(Character.isUpperCase(s.charAt(0))) {
			String f=s.toUpperCase();
			System.out.println(f);
			
		}
		else {
			String l= s.toLowerCase();
			System.out.println(l);
		}
		
		

	}

}
